import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import FeatureCard from '../components/FeatureCard';
import LoadingSpinner from '../components/LoadingSpinner';

const HomePage = () => {
  const { user } = useAuth();
  const [isLoading, setIsLoading] = useState(true);
  const [stats, setStats] = useState(null);

  // Simulação de carregamento de dados
  useEffect(() => {
    const timer = setTimeout(() => {
      setStats({
        totalQuestions: 1243,
        totalAnswers: 3567,
        activeMentors: 42,
      });
      setIsLoading(false);
    }, 1500);

    return () => clearTimeout(timer);
  }, []);

  const features = [
    {
      icon: 'bi-question-circle',
      title: 'Tire suas dúvidas',
      description: 'Poste suas questões de programação e receba ajuda da comunidade'
    },
    {
      icon: 'bi-lightbulb',
      title: 'Ajude outros',
      description: 'Compartilhe seu conhecimento respondendo perguntas'
    },
    {
      icon: 'bi-award',
      title: 'Ganhe pontos',
      description: 'Acumule pontos e troque por recompensas exclusivas'
    }
  ];

  if (isLoading) {
    return <LoadingSpinner fullPage />;
  }

  return (
    <div className="home-page">
      {/* Hero Section */}
      <section className="hero-section bg-primary text-white py-5">
        <div className="container py-4">
          <div className="row align-items-center">
            <div className="col-lg-6">
              <h1 className="display-4 fw-bold mb-3">
                {user ? `Bem-vindo de volta, ${user.username}!` : 'Aprenda e Ensine Programação'}
              </h1>
              <p className="lead mb-4">
                Conecte-se com desenvolvedores de todos os níveis e acelere seu aprendizado
              </p>
              <div className="d-flex gap-3">
                {user ? (
                  <Link to="/dashboard" className="btn btn-light btn-lg px-4">
                    Meu Dashboard
                  </Link>
                ) : (
                  <>
                    <Link to="/register" className="btn btn-light btn-lg px-4">
                      Criar Conta
                    </Link>
                    <Link to="/login" className="btn btn-outline-light btn-lg px-4">
                      Login
                    </Link>
                  </>
                )}
              </div>
            </div>
            <div className="col-lg-6 d-none d-lg-block">
              <img 
                src="https://images.unsplash.com/photo-1555066931-4365d14bab8c?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60" 
                alt="Programação" 
                className="img-fluid rounded shadow"
              />
            </div>
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="stats-section py-4 bg-light">
        <div className="container">
          <div className="row text-center">
            <div className="col-md-4">
              <h2 className="display-5 fw-bold text-primary">{stats.totalQuestions}+</h2>
              <p className="text-muted">Perguntas Respondidas</p>
            </div>
            <div className="col-md-4">
              <h2 className="display-5 fw-bold text-primary">{stats.totalAnswers}+</h2>
              <p className="text-muted">Soluções Postadas</p>
            </div>
            <div className="col-md-4">
              <h2 className="display-5 fw-bold text-primary">{stats.activeMentors}+</h2>
              <p className="text-muted">Mentores Ativos</p>
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="features-section py-5">
        <div className="container">
          <h2 className="text-center mb-5">Como funciona a plataforma</h2>
          <div className="row g-4">
            {features.map((feature, index) => (
              <div key={index} className="col-md-4">
                <FeatureCard {...feature} />
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Call to Action */}
      <section className="cta-section py-5 bg-dark text-white">
        <div className="container text-center py-4">
          <h2 className="mb-4">Pronto para fazer parte da comunidade?</h2>
          <Link 
            to={user ? "/questions" : "/register"} 
            className="btn btn-primary btn-lg px-5"
          >
            {user ? 'Explorar Perguntas' : 'Comece Agora'}
          </Link>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
